import{_ as p,o as t,c as o,a as n}from"./index.24f9f7c9.js";const r={},x={class:"wrap"},a=n('<div class="text"><p style="text-align:center;"><strong><span style="font-size:40px;">\u8F6F\u4EF6\u670D\u52A1\u53CA\u7528\u6237\u8BB8\u53EF\u534F\u8BAE</span></strong></p><p><strong><span style="font-size:40px;"><br></span></strong></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;font-size:20px;font-weight:bold;"> \u6B22\u8FCE\u60A8\u4F7F\u7528FBS\u8F6F\u4EF6\u53CA\u670D\u52A1\uFF01</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> \u5728\u60A8\u8BBF\u95EEhenqy.cn\u7F51\u7AD9,\u4E0B\u8F7D\u5B89\u88C5\u5E76\u4F7F\u7528\u672C\u5E94\u7528\uFF0C\u60A8\u5E94\u5F53\u9605\u8BFB\u5E76\u9075\u5B88\u300AFBS\u8F6F\u4EF6\u670D\u52A1\u53CA\u7528\u6237\u8BB8\u53EF\u534F\u8BAE\u300B(\u4EE5\u4E0B\u7B80\u79F0\u201C\u672C\u534F\u8BAE\u201D)\u3002 \u8BF7\u52A1\u5FC5\u5BA1\u614E\u9605\u8BFB\u3001\u5145\u5206\u7406\u89E3\u5404\u6761\u6B3E\u5185\u5BB9\uFF0C\u7279\u522B\u662F\u514D\u9664\u6216\u9650\u5236\u8D23\u4EFB\u7684\u6761\u6B3E\uFF0C\u4EE5\u53CA\u5F00\u901A\u6216\u4F7F\u7528\u67D0\u9879\u670D\u52A1\u7684\u5355\u72EC\u534F\u8BAE\u3002 \u60A8\u7684\u4F7F\u7528\u53CA\u767B\u5F55\u7B49\u884C\u4E3A\u5373\u89C6\u4E3A\u60A8\u5DF2\u9605\u8BFB\u5E76\u540C\u610F\u4E0A\u8FF0\u534F\u8BAE\u7684\u7EA6\u675F\u3002</p><p><span style="font-size:20px;font-weight:bold;">\u4E00\u3001\u521B\u5EFA\u8D26\u6237\u548C\u4F7F\u7528</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> 1.1 \u60A8\u53EF\u4EE5\u81EA\u7531\u7BA1\u7406\u60A8\u7684\u8D26\u6237\u548C\u4F7F\u7528\uFF0C\u4F46\u4EC5\u9650\u60A8\u516C\u53F8\u4F7F\u7528\uFF0C\u672C\u5E94\u7528\u4E0D\u53EF\u4EE5\u4F5C\u4E3A\u5E73\u53F0\u6027\u8D28\u7684\u57FA\u7840\u8F6F\u4EF6\u5411\u5916\u5206\u53D1\u8D26\u53F7\u3002 </p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> 1.2 \u60A8\u4E0D\u80FD\u4F7F\u7528\u60A8\u7684\u8D26\u6237\u6216\u8F6F\u4EF6\u8FDB\u884C\u4EFB\u4F55\u8FDD\u6CD5\u884C\u4E3A(\u5305\u62EC\u6240\u6709\u6CD5\u5F8B\uFF1A\u4E0D\u53EA\u9650\u4E8E\u77E5\u8BC6\u4EA7\u6743\u6CD5)\u3002</p><p><span style="font-size:20px;font-weight:bold;">\u4E8C\u3001\u8F6F\u4EF6\u7684\u66F4\u65B0\u53CA\u7EF4\u62A4</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">2.1 \u4E3A\u4E86\u6539\u5584\u7528\u6237\u4F53\u9A8C\u3001\u5B8C\u5584\u670D\u52A1\u5185\u5BB9,FBS \u5C06\u4E0D\u65AD\u52AA\u529B\u5F00\u53D1\u65B0\u7684\u529F\u80FD\u53CA\u670D\u52A1\uFF0C\u5E76\u4E3A\u60A8\u4E0D\u5B9A\u671F\u63D0\u4F9B\u8F6F\u4EF6\u66F4\u65B0(\u8FD9\u4E9B\u66F4\u65B0\u53EF\u80FD\u4F1A\u91C7\u53D6\u8F6F\u4EF6\u529F\u80FD\u66FF\u6362\u3001\u5F3A\u5316\u3001\u4FEE\u6539\u3001\u53BB\u9664\uFF0C\u7248\u672C\u5347\u7EA7\u7B49\u5F62\u5F0F)\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">2.2 \u60A8\u6709\u6743\u9009\u62E9\u5BF9\u8F6F\u4EF6\u8FDB\u884C\u66F4\u65B0\uFF0C\u4F46\u5E94\u91CD\u590D\u8BC4\u4F30\u7248\u672C\u4E4B\u95F4\u7684\u5DEE\u5F02\uFF0C\u56E0\u7248\u672C\u4E0D\u540C\u5BF9\u60A8\u516C\u53F8\u4E1A\u52A1\u9020\u6210\u7684\u635F\u5931\uFF0C\u672C\u8F6F\u4EF6\u4E0D\u627F\u62C5\u4EFB\u4F55\u8D23\u4EFB\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">2.3 \u672C\u8F6F\u4EF6\u65B0\u7248\u672C\u53D1\u5E03\u540E\uFF0C\u65E7\u7684\u529F\u80FD\u53EF\u80FD\u65E0\u6CD5\u4F7F\u7528\u6216\u6539\u53D8\u4E86\u65E7\u529F\u80FD\u7684\u64CD\u4F5C\u65B9\u5F0F\u548C\u5C55\u73B0\u5F62\u5F0F,BFS\u4E0D\u4FDD\u8BC1\u672C\u8F6F\u4EF6\u7684\u67D0\u4E2A\u529F\u80FD\u4F1A\u4E00\u76F4\u4FDD\u7559\u5B58\u5728\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">2.4 \u672C\u8F6F\u4EF6\u5728\u7248\u672C\u5347\u7EA7\u6216\u7EF4\u62A4\u671F\u95F4\u9700\u8981\u505C\u6B62\u672C\u670D\u52A1\u3002\u8BE5\u884C\u4E3A\u5C5E\u4E8E\u6B63\u5E38\u7684\u4F8B\u884C\u5DE5\u4F5C\uFF0C\u60A8\u7406\u89E3\u5E76\u540C\u610FFBS\u5C06\u4F1A\u5C3D\u5176\u5546\u4E1A\u548C\u6280\u672F\u4E0A\u7684\u5408\u7406\u52AA\u529B\u4FDD\u969C\u60A8\u5728\u505C\u6B62\u670D\u52A1\u671F\u95F4\u53D7\u5230\u6700\u5C0F\u5F71\u54CD\u3002\u4F46\u662F\uFF0CFBS\u5E76\u4E0D\u627F\u62C5\u505C\u6B62\u670D\u52A1\u671F\u95F4\u6240\u9020\u6210\u7684\u4EFB\u4F55\u7528\u6237\u635F\u5931\u3002 </p><p><span style="font-size:20px;font-weight:bold;">\u4E09\u3001\u7528\u6237\u4FE1\u606F\u4FDD\u62A4</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> 3.1\u4FDD\u62A4\u7528\u6237\u662FFBS\u7684\u4E00\u9879\u57FA\u672C\u539F\u5219\uFF0CFBS\u5C06\u4F1A\u91C7\u53D6\u5408\u7406\u7684\u63AA\u65BD\u4FDD\u62A4\u7528\u6237\u7684\u4FE1\u606F\u3002\u672C\u8F6F\u4EF6\u4E0D\u4F1A\u6536\u96C6\u4EFB\u4F55\u7528\u6237\u4FE1\u606F\uFF0C\u7528\u6237\u5BF9\u81EA\u5DF1\u7684\u6570\u636E\u4EAB\u6709\u5145\u5206\u7684\u6743\u5229\u3002 </p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> 3.2\u60A8\u5728\u4F7F\u7528\u672C\u8F6F\u4EF6\u65F6\uFF0C\u5E94\u5145\u5206\u8BC4\u4F30\u672C\u8F6F\u4EF6\u5BF9\u60A8\u7684\u4E1A\u52A1\u5F71\u54CD\u3002\u60A8\u5E94\u8BE5\u5404\u79CD\u5B89\u5168\u6280\u672F\u548C\u7A0B\u5E8F\u5EFA\u7ACB\u5B8C\u5584\u7684\u7BA1\u7406\u5236\u5EA6\u6765\u4FDD\u62A4\u60A8\u7684\u7528\u6237\u4FE1\u606F\uFF0C\u4EE5\u514D\u906D\u53D7\u672A\u7ECF\u6388\u6743\u7684\u8BBF\u95EE\u3001\u4F7F\u7528\u6216\u62AB\u9732\u3002\u5F53\u60A8\u7684\u6570\u636E\u53D1\u751F\u4E22\u5931\uFF0C\u6CC4\u9732\u7B49\u60C5\u51B5\uFF0C\u672C\u8F6F\u4EF6\u4E0D\u8D1F\u4EFB\u4F55\u8D23\u4EFB\u3002 </p><p><span style="font-size:20px;font-weight:bold;">\u56DB\u3001\u4E3B\u6743\u5229\u4E49\u52A1\u6761\u6B3E</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.1 FBS\u8F6F\u4EF6\u8457\u4F5C\u6743\uFF0C\u5546\u6807\u6743\u7B49\u5F52henqy.cn\u6240\u6709\uFF0C\u60A8\u4EC5\u83B7\u5F97\u672C\u670D\u52A1\u53CA\u672C\u8F6F\u4EF6\u7684\u4F7F\u7528\u6743,\u4E14\u8BE5\u4F7F\u7528\u6743\u4EC5\u5C5E\u4E8E\u7533\u8BF7\u6CE8\u518C\u4EBA\u672C\u4EBA\u53CA\u7533\u8BF7\u6CE8\u518C\u4EBA\u6388\u6743\u7684\u5B50\u8D26\u53F7\u4F7F\u7528\u8005\uFF08\u4EE5\u4E0B\u7B80\u79F0\u201C\u7528\u6237\u201D\u3002\u88AB\u6388\u6743\u5B50\u8D26\u53F7\u4F7F\u7528\u8005\u7684\u4EFB\u4F55\u8FDD\u53CD\u672C\u534F\u8BAE\u7684\u884C\u4E3A\u5C06\u7531\u7533\u8BF7\u6CE8\u518C\u4EBA\u672C\u4EBA\u627F\u62C5\u6240\u6709\u8D23\u4EFB\uFF09\u3002\u540C\u65F6\uFF0C\u7528\u6237\u4E0D\u5F97\u6216\u552E\u5356FBS\u8F6F\u4EF6. </p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.2 \u60A8\u7406\u89E3\u5E76\u540C\u610FFBS\u5C06\u4F1A\u5C3D\u5176\u5546\u4E1A\u4E0A\u7684\u5408\u7406\u52AA\u529B\u4FDD\u969C\u60A8\u5728\u672C\u8F6F\u4EF6\u53CA\u670D\u52A1\u4E2D\u7684\u6570\u636E\u5B58\u50A8\u5B89\u5168\uFF0C\u4F46\u662F\uFF0CFBS\u5E76\u4E0D\u80FD\u5C31\u6B64\u63D0\u4F9B\u5B8C\u5168\u4FDD\u8BC1\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\u4EE5\u4E0B\u60C5\u5F62\uFF1A</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.2.1 FBS\u4E0D\u5BF9\u60A8\u5728\u672C\u8F6F\u4EF6\u53CA\u670D\u52A1\u4E2D\u76F8\u5173\u6570\u636E\u7684\u5220\u9664\u6216\u5B58\u50A8\u5931\u8D25\u8D1F\u8D23\uFF1B</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.2.2 \u5982\u679C\u60A8\u505C\u6B62\u6216\u53D6\u6D88\u4F7F\u7528\u672C\u8F6F\u4EF6\u53CA\u670D\u52A1\uFF0C\u8BF7\u60A8\u52A1\u5FC5\u4FDD\u5B58\u597D\u60A8\u7684\u6570\u636E\uFF0CFBS\u4E0D\u4FDD\u8BC1\u6570\u636E\u7684\u5B8C\u6574\uFF0C\u540C\u65F6\u4E5F\u6CA1\u4E49\u52A1\u7EE7\u7EED\u5411\u60A8\u670D\u52A1\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.3 \u7528\u6237\u5728\u4F7F\u7528\u672C\u8F6F\u4EF6\u53CA\u670D\u52A1\u65F6\uFF0C\u987B\u81EA\u884C\u627F\u62C5\u5982\u4E0B\u6765\u81EAFBS\u4E0D\u53EF\u638C\u63A7\u7684\u98CE\u9669\u5185\u5BB9\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E:</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">4.3.1 \u7531\u4E8E\u4E0D\u53EF\u6297\u62D2\u56E0\u7D20\u53EF\u80FD\u5F15\u8D77\u7684\u7528\u6237\u6570\u636E\u7684\u4E22\u5931\u98CE\u9669\uFF1B</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"> 4.3.2\u7528\u6237\u5728\u4F7F\u7528\u672C\u8F6F\u4EF6\u53CA\u670D\u52A1\u7684\u8FC7\u7A0B\u4E2D,\u56E0\u64CD\u4F5C\u4E0D\u5F53\u9020\u6210\u4E1A\u52A1\u6570\u636E\u9519\u4E71,\u4E22\u5931\u7B49\u800C\u5F15\u53D1\u7684\u4EFB\u4F55\u635F\u5931,FBS\u4E0D\u627F\u62C5\u4EFB\u4F55\u8D23\u4EFB\u3002</p><p><span style="font-size:20px;font-weight:bold;">\u4E94\u3001\u77E5\u8BC6\u4EA7\u6743\u58F0\u660E</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">5.1 henqy.cn\u662F\u672C\u8F6F\u4EF6\u7684\u77E5\u8BC6\u4EA7\u6743\u6743\u5229\u4EBA\u3002\u672C\u8F6F\u4EF6\u7684\u4E00\u5207\u8457\u4F5C\u6743\u3001\u5546\u6807\u6743\u3001\u4E13\u5229\u6743\u3001\u5546\u4E1A\u79D8\u5BC6\u7B49\u77E5\u8BC6\u4EA7\u6743\uFF0C\u4EE5\u53CA\u4E0E\u672C\u8F6F\u4EF6\u76F8\u5173\u7684\u6240\u6709\u4FE1\u606F\u5185\u5BB9\uFF08\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\u6587\u5B57\u3001\u56FE\u7247\u3001\u89C6\u9891\u3001\u56FE\u8868\u3001\u754C\u9762\u8BBE\u8BA1\u3001\u7248\u9762\u6846\u67B6\u3001\u6709\u5173\u6570\u636E\u6216\u7535\u5B50\u6587\u6863\u7B49\uFF09\u5747\u53D7\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u6CD5\u5F8B\u6CD5\u89C4\u548C\u76F8\u5E94\u7684\u56FD\u9645\u6761\u7EA6\u4FDD\u62A4\uFF0Chenqy.cn \u4EAB\u6709\u4E0A\u8FF0\u77E5\u8BC6\u4EA7\u6743\uFF0C\u4F46\u76F8\u5173\u6743\u5229\u4EBA\u4F9D\u7167\u6CD5\u5F8B\u89C4\u5B9A\u5E94\u4EAB\u6709\u7684\u6743\u5229\u9664\u5916\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">5.2 \u672A\u7ECFFBS\u6216\u76F8\u5173\u6743\u5229\u4EBA\u4E66\u9762\u540C\u610F\uFF0C\u60A8\u4E0D\u5F97\u4E3A\u4EFB\u4F55\u5546\u4E1A\u6216\u975E\u5546\u4E1A\u76EE\u7684\u81EA\u884C\u6216\u8BB8\u53EF\u4EFB\u4F55\u7B2C\u4E09\u65B9\u5B9E\u65BD\u3001\u5229\u7528\u3001\u8F6C\u8BA9\u4E0A\u8FF0\u77E5\u8BC6\u4EA7\u6743\u3002</p><p><span style="font-size:20px;font-weight:bold;">\u516D\u3001\u5176\u4ED6</span></p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">6.1 \u60A8\u4F7F\u7528\u672C\u8F6F\u4EF6\u5373\u89C6\u4E3A\u60A8\u5DF2\u9605\u8BFB\u5E76\u540C\u610F\u53D7\u672C\u534F\u8BAE\u7684\u7EA6\u675F\uFF0CFBS \u6709\u6743\u5728\u5FC5\u8981\u65F6\u4FEE\u6539\u672C\u534F\u8BAE\u6761\u6B3E\u3002\u60A8\u53EF\u4EE5\u5728\u672C\u8F6F\u4EF6\u7684\u6700\u65B0\u7248\u672C\u4E2D\u67E5\u9605\u76F8\u5173\u534F\u8BAE\u6761\u6B3E\u3002\u672C\u534F\u8BAE\u6761\u6B3E\u53D8\u66F4\u540E\uFF0C\u5982\u679C\u60A8\u7EE7\u7EED\u4F7F\u7528\u672C\u8F6F\u4EF6\uFF0C\u5373\u89C6\u4E3A\u60A8\u5DF2\u63A5\u53D7\u4FEE\u6539\u540E\u7684\u534F\u8BAE\u3002\u5982\u679C\u60A8\u4E0D\u63A5\u53D7\u4FEE\u6539\u540E\u7684\u534F\u8BAE\uFF0C\u5E94\u5F53\u505C\u6B62\u4F7F\u7528\u672C\u8F6F\u4EF6\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">6.2 FBS \u53EA\u786E\u4FDD\u7ED9\u4ED8\u8D39\u5BA2\u6237\u901A\u8FC7\u7535\u5B50\u90AE\u4EF6\u3001\u7535\u8BDD\u3001\u73B0\u573A\u8FDB\u884C\u6280\u672F\u652F\u63F4\u6216\u57F9\u8BAD\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">6.3 \u672C\u534F\u8BAE\u7684\u6210\u7ACB\u3001\u751F\u6548\u3001\u5C65\u884C\u3001\u89E3\u91CA\u53CA\u7EA0\u7EB7\u89E3\u51B3\uFF0C\u9002\u7528\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u3002 </p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">6.4 \u82E5\u60A8\u548Chenqy.cn\u4E4B\u95F4\u53D1\u751F\u4EFB\u4F55\u7EA0\u7EB7\u6216\u4E89\u8BAE\uFF0C\u9996\u5148\u5E94\u53CB\u597D\u534F\u5546\u89E3\u51B3\uFF1B\u534F\u5546\u4E0D\u6210\u7684\uFF0C\u60A8\u540C\u610F\u5C06\u7EA0\u7EB7\u6216\u4E89\u8BAE\u63D0\u4EA4\u672C\u534F\u8BAE\u7B7E\u8BA2\u5730\u6709\u7BA1\u8F96\u6743\u7684\u4EBA\u6C11\u6CD5\u9662\u7BA1\u8F96\u3002</p><p style="margin-top:0px;margin-bottom:0px;padding:0px;border:0px;">6.5 \u672C\u534F\u8BAE\u6761\u6B3E\u65E0\u8BBA\u56E0\u4F55\u79CD\u539F\u56E0\u90E8\u5206\u65E0\u6548\u6216\u4E0D\u53EF\u6267\u884C\uFF0C\u5176\u4F59\u6761\u6B3E\u4ECD\u6709\u6548\uFF0C\u5BF9\u53CC\u65B9\u5177\u6709\u7EA6\u675F\u529B\u3002</p><p style="text-align:right;margin-top:0px;margin-bottom:0px;padding:0px;border:0px;"><span style="font-size:20px;font-weight:bold;">henqy.cn</span></p><p><br></p></div>',1),e=[a];function i(d,g){return t(),o("div",x,e)}const m=p(r,[["render",i]]);export{m as default};
